# Primer Clone All Repo's, Cleanup, & Install
# ===========================================

#!/system/bin/sh

echo "Make Primer Directory"
mkdir -p Primer

echo "Move into Primer Folder"
cd Primer

echo "Clone All Primer Repo's with curl"
curl "https://api.github.com/orgs/primer/repos?per_page=1000" | grep -o 'git@[^"]*' | xargs -L1 git clone; open .

echo "Cleaning up the Repository"
